import {useState, useEffect} from 'react'
import getToday from '../../helpers/getToday'

export default function Time(){
    const tempToday = getToday()
    const [today, setToday] = useState(tempToday)

    useEffect(() => {
        const t = setInterval(() => {
            setToday(getToday())
        },1000)
        return () => {
clearInterval(t)
        }
    },[])

    return (
        <div className='time'>{`${today}0`}</div>
    )
}
