import {useEffect, useState} from 'react'
import ActiveCountry from '../ActiveCountry'
import Header from '../header'
import { Button } from "@chakra-ui/react"
import Loading from '../Loading'
import { Input } from "@chakra-ui/react"


export default function Main({isMobile}){
    const [data, setData] = useState([])
    const [isLoading, setIsLoading] = useState(true)
    const [active, setActive] = useState(false)
    const [filters,setFilters] = useState('Vietnam')
    const filter = (country, filters) => {
        return country.country ==  filters
    }
    const handleChange = (event) => {
        setFilters(event.target.value) 
    }

    useEffect(() => {
        const requestUrl = 'https://corona.lmao.ninja/v2/countries'
        fetch(requestUrl).then((response) => {
        return (response.json());
        }).then((data) => {  
            setData(data)
            setIsLoading(false) 
            setActive(data[214])
        })
    },[])




    return (
        <>
        <Header />
        {isLoading && <Loading/>}

        {!isLoading && <div className='main'>
                            <div >
                                {active && <ActiveCountry activeCountry={active}/>}
                            </div>


                            {(<div className='table'>
                                <div className='table-header'>LIST OF COUNTRIES OF THE WORLD</div>
                                <div className='row'>
                                    <div >COUNTRY
                                        <Input onChange={handleChange} placeholder="Enter country" />
                                    </div>
                                    <div >COMTINENTS</div>
                                    <div >INFO</div>
                                </div>
                                <ul>
                                    {data.filter((country) => filter(country, filters)).map((country, idx) => 
                                        <li className='row' key={`${country.countryInfo.iso3}-${idx}`} >
                                            <div>{country.country}</div>
                                            <div>{country.continent}</div>
                                            <div className='btn'><Button onClick={() => setActive(country)}>Details</Button></div>
                                        </li> 
                                    )}
                                </ul>
                            </div>)}


                        </div>}


        </>
    )
}
